import { combineReducers } from 'redux';
import types from './contacts-type';

const items = (state = [], { type, payload }) => {
  switch (type) {
    case type.ADD:
      return [...state, payload];
    default:
      return state;
  }
};

const filter = (state = [], actions) => {
  return state;
};

export default combineReducers({
  items,
  filter,
});
