import React from 'react';
import { connect } from 'react-redux';
import contactsActions from '../../../redux/contacts-actions'
import PropTypes from 'prop-types'
import ContactsListItem from '../ContactListItem'



function ContactList({items, onDeleteContacts}) {
  const contactElements = items.map(({ id, ...props }) => (
      <ContactsListItem key={id}  {...props} onClick={()=> onDeleteContacts(id)}
      />
    ))

    return (
        <ul>
            {contactElements}
        </ul>
    )
};

// const getVisibleContacts = (allContacts, filter) => {
//     const normalizedFilter = filter.toLowerCase();

//     return allContacts.filter(({name}) => name.toLowerCase().includes(normalizedFilter));
//   };

// const mapStateToProps = ({ contacts: { items, filter}}) => ({
//   contacts: getVisibleContacts(items, filter)
// })

const mapStateToProps = (state) => {
    const { filter, items } = state.contacts;
    const normalizedFilter = filter.toLowerCase();
    const filteredContacts = items.filter(item => item.name.toLowerCase().includes(normalizedFilter))
    return {
        items: filteredContacts,
    }
}

const mapDispatchToProps = dispatch => ({
  onDeleteContacts: (id) => dispatch (contactsActions.deleteContact(id))
}) 


export default connect(mapStateToProps, mapDispatchToProps)(ContactList);

ContactList.propTypes = {
    contacts: PropTypes.arrayOf(
      PropTypes.shape({
        id: PropTypes.string.isRequired,
        name: PropTypes.string.isRequired,
        number: PropTypes.string.isRequired,
      }).isRequired,
    ),
    onDeleteContacts: PropTypes.func.isRequired,
  };
